import React from "react";
import UserList from "./UserList";
import Chat from "./Chat";

const LoggedInHome: React.FC = () => {
  return (
    <div style={{
      display: "flex",
      height: "100vh",
      background: "#f5f6fa"
    }}>
      {/* Friends List */}
      <div style={{
        flex: 1,
        background: "#fff",
        borderRight: "1px solid #eee",
        padding: "1em",
        minWidth: "220px"
      }}>
        <h3>Friends</h3>
        <UserList />
      </div>

      {/* Active Chat */}
      <div style={{
        flex: 2,
        padding: "1em",
        background: "#fafafa"
      }}>
        <h3>Active Chat</h3>
        <Chat />
      </div>

      {/* Settings */}
      <div style={{
        flex: 1,
        background: "#fff",
        borderLeft: "1px solid #eee",
        padding: "1em",
        minWidth: "220px"
      }}>
        <h3>Settings</h3>
        <button
          style={{
            background: "#4078c0",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
            padding: "0.5em 1em",
            cursor: "pointer"
          }}
          onClick={() => {
            window.location.href = "/login";
          }}
        >
          Logout
        </button>
        {/* Add more settings here */}
      </div>
    </div>
  );
};

export default LoggedInHome;
